sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    let percentage = api.elements.amount_you_have / api.elements.recipe_amount;

    var words = api.state.base_ingredients.replace(/\n/g, " \n ").split(' ');
    var replaced = [];
    words.forEach(w => !isNaN(w) && w !== `\n` ? replaced.push(Math.round(+w * percentage * 100) / 100) : replaced.push(w));
    var result = replaced.join(' ');

    api.setState('ingredients_to_display', result);
}
